package de.drinkndrive.web.rest;

import com.codahale.metrics.annotation.Timed;
import de.drinkndrive.domain.Track;
import de.drinkndrive.repository.TrackRepository;
import de.drinkndrive.web.rest.util.PaginationUtil;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.data.domain.Page;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import javax.inject.Inject;
import java.net.URI;
import java.net.URISyntaxException;
import java.util.List;
import java.util.Optional;

/**
 * REST controller for managing Track.
 */
@RestController
@RequestMapping("/api")
public class TrackResource {

    private final Logger log = LoggerFactory.getLogger(TrackResource.class);

    @Inject
    private TrackRepository trackRepository;

    /**
     * POST  /tracks -> Create a new track.
     */
    @RequestMapping(value = "/tracks",
            method = RequestMethod.POST,
            produces = MediaType.APPLICATION_JSON_VALUE)
    @Timed
    public ResponseEntity<Void> create(@RequestBody Track track) throws URISyntaxException {
        log.debug("REST request to save Track : {}", track);
        if (track.getId() != null) {
            return ResponseEntity.badRequest().header("Failure", "A new track cannot already have an ID").build();
        }
        trackRepository.save(track);
        return ResponseEntity.created(new URI("/api/tracks/" + track.getId())).build();
    }

    /**
     * PUT  /tracks -> Updates an existing track.
     */
    @RequestMapping(value = "/tracks",
        method = RequestMethod.PUT,
        produces = MediaType.APPLICATION_JSON_VALUE)
    @Timed
    public ResponseEntity<Void> update(@RequestBody Track track) throws URISyntaxException {
        log.debug("REST request to update Track : {}", track);
        if (track.getId() == null) {
            return create(track);
        }
        trackRepository.save(track);
        return ResponseEntity.ok().build();
    }

    /**
     * GET  /tracks -> get all the tracks.
     */
    @RequestMapping(value = "/tracks",
            method = RequestMethod.GET,
            produces = MediaType.APPLICATION_JSON_VALUE)
    @Timed
    public ResponseEntity<List<Track>> getAll(@RequestParam(value = "page" , required = false) Integer offset,
                                  @RequestParam(value = "per_page", required = false) Integer limit)
        throws URISyntaxException {
        Page<Track> page = trackRepository.findAll(PaginationUtil.generatePageRequest(offset, limit));
        HttpHeaders headers = PaginationUtil.generatePaginationHttpHeaders(page, "/api/tracks", offset, limit);
        return new ResponseEntity<>(page.getContent(), headers, HttpStatus.OK);
    }

    /**
     * GET  /tracks/:id -> get the "id" track.
     */
    @RequestMapping(value = "/tracks/{id}",
            method = RequestMethod.GET,
            produces = MediaType.APPLICATION_JSON_VALUE)
    @Timed
    public ResponseEntity<Track> get(@PathVariable Long id) {
        log.debug("REST request to get Track : {}", id);
        return Optional.ofNullable(trackRepository.findOne(id))
            .map(track -> new ResponseEntity<>(
                track,
                HttpStatus.OK))
            .orElse(new ResponseEntity<>(HttpStatus.NOT_FOUND));
    }

    /**
     * DELETE  /tracks/:id -> delete the "id" track.
     */
    @RequestMapping(value = "/tracks/{id}",
            method = RequestMethod.DELETE,
            produces = MediaType.APPLICATION_JSON_VALUE)
    @Timed
    public void delete(@PathVariable Long id) {
        log.debug("REST request to delete Track : {}", id);
        trackRepository.delete(id);
    }
}
