"use strict";
define( [
  '../../../../bower_components/underscore/underscore',
  'backbone',
	'models/setting',
	'config'
], function( _, Backbone, SettingModel, Config ){

	var SettingsCollection = Backbone.Collection.extend({
		model : SettingModel,
		learnerId: null,
		url : function() {
			return Config.apiURL + "/learners/" + this.learnerId + '/settings';
		},
		parse: function( response ) {

			_.each( response.settings, function( v ) {
				v.id = v.settingId;
			} );

			return response.settings;
		}
	} );

	return SettingsCollection;
} );
