define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var ActivityModel = Backbone.Model.extend( {
			url: function() {
				return Config.apiURL + '/learners/activity/' + this.id;
			},
			defaults: function() {
				return {
					id: '',
					type: '',
					typeId: null,
					month: null,
					title: '',
					description: null,
					image: null,
					url: null,
					status: '',
					dateTime: null,
					activityTypeId: null,
					monthlyStats: null,
					paths: []
				};
			}
    } );

    return ActivityModel;
});
