define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var CreateLearnerModel = Backbone.Model.extend( {
			url: function() {
				return Config.apiURL + '/learners/' + this.id;
			},
			defaults: function() {
				return {
					id: '',
					givenName: '',
					familyName: '',
					email: '',
					dateOfBirth: null,
					drivingTestDate: null,
					signupDate: null,
					picture: '',
					accessToken: '',
					dateOfBirthString: '',
					image: null
				};
			}
    } );

    return CreateLearnerModel;
});
