define( [
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'i18next',
		'config'
], function( _, Backbone, i18next, config ){
    var EditLearnerModel = Backbone.Model.extend( {
			url: function() {
				return config.apiURL + '/learners/' + this.id;
			},
			defaults: function() {
				return {
					id: '',
					givenName: '',
					familyName: '',
					email: '',
					dateOfBirth: null,
					chooseGoal: null,
					signupDate: null,
					picture: '',
					pictureLarge: '',
					accessToken: '',
					dateOfBirthString: '',
					image: null
				};
			}
    } );

    return EditLearnerModel;
});
