define( [
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'config'
], function( _, Backbone, config ){
    var InstructorModel = Backbone.Model.extend( {
			url: function() {
				return config.apiURL + '/instructors/' + this.id;
			},
			defaults: function() {
				return {
					id: '',
					givenName: '',
					familyName: '',
					email: '',
					telephone: '',
					isMale: false,
					permalink: '',
					grade: '',
					instructorStatus: '',
					transmission: '',
					currency: '',
					hourlyRate: '',
					averageLessonRating: 0,
					averageReviews: 0,
					isApproved: false,
					leadCount: 0,
					imageCount: 0,
					hasPrice: false,
					bioCompletion: 0,
					postcode: '',
					areaCovered: '',
					town: '',
					region: '',
					postcode: '',
					radius: 0,
					qualifiedAt: '',
					image: '',
					registrationNumber : '',
					model : '',
					make : '',
					latitude : '',
					longitude : '',
					isFranchise : false,
					biographyIntroduction: '',
					biographyIntroductionShort: '',
					biographyAbout: '',
					biographyStyle: '',
					biographyOther: '',
					reviews: [],
					images: [],
					fullSizeImages: [],
					guidePrices: [],
					paths: [],
					accessToken: ''
				};
			},

			parse: function( response ) {
				if ( response && response.biographyIntroduction ) {
					var textLength = 100;
					response.biographyIntroductionShort = ( response.biographyIntroduction.length > textLength ) ? response.biographyIntroduction.substr(0, ( textLength - 1 ) ) + '&hellip;' : response.biographyIntroduction;
				}

				return response;
			}
    } );
    return InstructorModel;
});
