define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var LearnerModel = Backbone.Model.extend( {
			url: function() {
				//return window.app.config.apiURL + '/learners/' + this.id;
				return Config.apiURL + '/learners/' + this.id;
			},
			defaults: function() {
				return {
					id: '',
					givenName: '',
					familyName: '',
					email: '',
					description: '',
					drivingTestDate: null,
					signupDate: null,
					type: '',
					image: null,
					picture: [],
					paths: [],
					stats: [],
					share: [],
					instructor: null,
					accessToken: '',
					journeyDate: null,
					chooseGoal: null
				};
			}
    } );

    return LearnerModel;
});
