define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var LearnerActivityModel = Backbone.Model.extend( {
			id: null,
			url: function() {
				return Config.apiURL + '/activities/' + this.id;
			},
			defaults: function() {
				return {
					typeId: null,
					learnerId:null
				};
			}
    } );

    return LearnerActivityModel;
});
