define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var SettingModel = Backbone.Model.extend( {
			learnerId: null,

			url: function() {
				return Config.apiURL + '/learners/' + this.learnerId + '/settings/' + this.id;
			},
			defaults: function() {
				return {
					id: '',
					learnerId: '',
					settingId: '',
					name: null,
					key: null,
					description: '',
					type: null,
					isEnabled: null,
					paths: []
				};
			}
    } );

    return SettingModel;
});
