define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'config'
], function( _, Backbone, Config ){
    var SignInModel = Backbone.Model.extend( {
			initialize: function( path ) {
				if ( path ) {
					this.path = path;
				}
			},
			path: '/login',
			url: function() {
				return Config.apiURL + this.path;
			},
			defaults: function() {
				return {
					email: "",
					password: ""
				};
			}
    } );
    return SignInModel;
});
