define( [
    'jquery',
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'i18next',
		'hbs!templates/createProfileModal'
], function( $, _, Backbone, i18next, createProfileModalTemplate ) {

	var CreateProfileModalView = Backbone.View.extend( {

		el: '#modal-content',

		events: {
			'click .photo': 'photo'
		},

		photo: function( e ) {

			var typePhoto = $( e.currentTarget ).data( 'type' );
			Backbone.Notifications.trigger('CallCamera', typePhoto);

		},

		render: function() {

			var compiledTemplate = createProfileModalTemplate();

			this.$el.html( compiledTemplate );

		}

	});

	return CreateProfileModalView;
} );
