define( [
    'jquery',
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'i18next',
		'hbs!templates/editProfileModal'
], function( $, _, Backbone, i18next, editProfileModalTemplate ) {

	var EditProfileModalView = Backbone.View.extend( {

		el: '#modal-content',

		events: {
			'click .photo': 'photo'
		},

		photo: function( e ) {

			var typePhoto = $( e.currentTarget ).data( 'type' );

			Backbone.Notifications.trigger('CallCamera', typePhoto);

		},

		render: function() {

			var compiledTemplate = editProfileModalTemplate();

			this.$el.html( compiledTemplate );


		}

	});

	return EditProfileModalView;
} );
