define( [
    'jquery',
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'i18next',
		'hbs!templates/instructorDetailModal'
], function( $, _, Backbone, i18next, instructorDetailModalTemplate ) {

	var InstructorDetailModalView = Backbone.View.extend( {

		el: '#modal-content',

		events: {
			'click .gallery': 'gallery',
			'click .choose': 'choose',
			'click .share': 'share'
		},

		gallery: function( e ) {

			Backbone.Notifications.trigger( 'InstructorAction', 'gallery' );

		},

		choose: function( e ) {

			Backbone.Notifications.trigger( 'InstructorAction', 'choose' );

		},

		share: function( e ) {

			Backbone.Notifications.trigger( 'InstructorAction', 'share' );

		},

		render: function() {

			var compiledTemplate = instructorDetailModalTemplate();

			this.$el.html( compiledTemplate );

		}

	});

	return InstructorDetailModalView;
} );
