define( [
	'jquery',
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'platform',
	'hbs!templates/profileInstructor'
], function( $, _, Backbone, i18next, Platform, ProfileInstructorTemplate ) {
	var ProfileInstructorView = Backbone.View.extend( {

		events: {
			'click': 'goThere'
		},

		render: function() {

			var self = this;
			var params = ( self.model ) ? self.model.toJSON() : {};

			compiledTemplate = ProfileInstructorTemplate( params );

			self.$el.html( compiledTemplate );
			self.delegateEvents();

			return self;
		},

		goThere: function( event ) {
			event.preventDefault();
			var self = this;

			if ( self.model ) {
				window.app.router.navigate( '/instructorDetail/' + self.model.id, true );
			} else {
				if ( Platform.platform == 'iOS' ) {
					window.app.router.navigate( '/instructorSearch', true );
				} else {
					window.app.router.navigate( '/instructors', true );
				}
			}
		}

	} );

	return ProfileInstructorView;
} );
