'use strict';

angular.module('drinkApp')
    .controller('LocationDetailController', function ($scope, $stateParams, Location, Track) {
        $scope.location = {};
        $scope.load = function (id) {
            Location.get({id: id}, function(result) {
              $scope.location = result;
            });
        };
        $scope.load($stateParams.id);
    });
