'use strict';

angular.module('drinkApp')
    .controller('TrackDetailController', function ($scope, $stateParams, Track, User) {
        $scope.track = {};
        $scope.load = function (id) {
            Track.get({id: id}, function(result) {
              $scope.track = result;
            });
        };
        $scope.load($stateParams.id);
    });
