'use strict';

angular.module('drinkApp')
    .controller('TrackController', function ($scope, Track, User, ParseLinks) {
        $scope.tracks = [];
        $scope.users = User.query();
        $scope.page = 1;
        $scope.loadAll = function() {
            Track.query({page: $scope.page, per_page: 20}, function(result, headers) {
                $scope.links = ParseLinks.parse(headers('link'));
                for (var i = 0; i < result.length; i++) {
                    $scope.tracks.push(result[i]);
                }
            });
        };
        $scope.reset = function() {
            $scope.page = 1;
            $scope.tracks = [];
            $scope.loadAll();
        };
        $scope.loadPage = function(page) {
            $scope.page = page;
            $scope.loadAll();
        };
        $scope.loadAll();

        $scope.create = function () {
            Track.update($scope.track,
                function () {
                    $scope.reset();
                    $('#saveTrackModal').modal('hide');
                    $scope.clear();
                });
        };

        $scope.update = function (id) {
            Track.get({id: id}, function(result) {
                $scope.track = result;
                $('#saveTrackModal').modal('show');
            });
        };

        $scope.delete = function (id) {
            Track.get({id: id}, function(result) {
                $scope.track = result;
                $('#deleteTrackConfirmation').modal('show');
            });
        };

        $scope.confirmDelete = function (id) {
            Track.delete({id: id},
                function () {
                    $scope.reset();
                    $('#deleteTrackConfirmation').modal('hide');
                    $scope.clear();
                });
        };

        $scope.clear = function () {
            $scope.track = {name: null, startTime: null, endTime: null, id: null};
            $scope.editForm.$setPristine();
            $scope.editForm.$setUntouched();
        };
    });
