define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var ManoeuvresModel = Backbone.Model.extend( {

			url: function() {
				return Config.apiURL + '/manoeuvres';
			},

			parse: function( response ) {
				if ( response.manoeuvres ) {
					try{
						window.localStorage.setItem( 'manoeuvres', JSON.stringify( response.manoeuvres ) );
					} catch( err ) {
						console.log( err );
					}
				}
			}
    } );

    return ManoeuvresModel;
});
