define( [
	'../../../../bower_components/underscore/underscore',
	'backbone',
	'i18next',
	'config'
], function( _, Backbone, i18next, Config ){
    var TheoryModel = Backbone.Model.extend( {
			url: function() {
				return Config.apiURL + '/learners/' + this.id + '/summaryquestions';
			},
			defaults: function() {
				return {
					id: ''
				};
			}
    } );

    return TheoryModel;
});
