define( [
    'jquery',
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'hbs!templates/activityMonth'
], function( $, _, Backbone, activityMonthTemplate ) {
	var ActivityMonthView = Backbone.View.extend( {
		tagName: 'div',
		className: 'month-header',
		initialize: function(){
			this.render();
		},

		render: function() {

			var self = this;
			var params = this.model || {};
			var compiledTemplate = activityMonthTemplate( params );

			self.$el.html( compiledTemplate );
			self.delegateEvents();

			return self;

		}

	} );

	return ActivityMonthView;
} );
