define( [
    'jquery',
    '../../../../bower_components/underscore/underscore',
    'backbone',
		'i18next',
		'hbs!templates/myInstructorModal'
], function( $, _, Backbone, i18next, myInstructorModalTemplate ) {

	var MyInstructorModalView = Backbone.View.extend( {

		el: '#modal-content',

		events: {
			'click .gallery': 'gallery',
			'click .change': 'change',
			'click .review': 'review'
		},

		gallery: function( e ) {

			Backbone.Notifications.trigger( 'InstructorAction', 'gallery' );

		},

		change: function( e ) {

			Backbone.Notifications.trigger( 'InstructorAction', 'change' );

		},

		review: function( e ) {

			Backbone.Notifications.trigger( 'InstructorAction', 'review' );

		},

		render: function() {

			var compiledTemplate = myInstructorModalTemplate();

			this.$el.html( compiledTemplate );

		}

	});

	return MyInstructorModalView;
} );
