'use strict';

angular.module('drinkApp')
    .controller('LocationController', function ($scope, Location, Track, ParseLinks) {
        $scope.locations = [];
        $scope.tracks = Track.query();
        $scope.page = 1;
        $scope.loadAll = function() {
            Location.query({page: $scope.page, per_page: 20}, function(result, headers) {
                $scope.links = ParseLinks.parse(headers('link'));
                for (var i = 0; i < result.length; i++) {
                    $scope.locations.push(result[i]);
                }
            });
        };
        $scope.reset = function() {
            $scope.page = 1;
            $scope.locations = [];
            $scope.loadAll();
        };
        $scope.loadPage = function(page) {
            $scope.page = page;
            $scope.loadAll();
        };
        $scope.loadAll();

        $scope.create = function () {
            Location.update($scope.location,
                function () {
                    $scope.reset();
                    $('#saveLocationModal').modal('hide');
                    $scope.clear();
                });
        };

        $scope.update = function (id) {
            Location.get({id: id}, function(result) {
                $scope.location = result;
                $('#saveLocationModal').modal('show');
            });
        };

        $scope.delete = function (id) {
            Location.get({id: id}, function(result) {
                $scope.location = result;
                $('#deleteLocationConfirmation').modal('show');
            });
        };

        $scope.confirmDelete = function (id) {
            Location.delete({id: id},
                function () {
                    $scope.reset();
                    $('#deleteLocationConfirmation').modal('hide');
                    $scope.clear();
                });
        };

        $scope.clear = function () {
            $scope.location = {lat: null, lng: null, alt: null, timestamp: null, speed: null, heading: null, id: null};
            $scope.editForm.$setPristine();
            $scope.editForm.$setUntouched();
        };
    });
