'use strict';

angular.module('drinkApp')
    .config(function ($stateProvider) {
        $stateProvider
            .state('track', {
                parent: 'entity',
                url: '/track',
                data: {
                    roles: ['ROLE_USER'],
                    pageTitle: 'drinkApp.track.home.title'
                },
                views: {
                    'content@': {
                        templateUrl: 'scripts/app/entities/track/tracks.html',
                        controller: 'TrackController'
                    }
                },
                resolve: {
                    translatePartialLoader: ['$translate', '$translatePartialLoader', function ($translate, $translatePartialLoader) {
                        $translatePartialLoader.addPart('track');
                        return $translate.refresh();
                    }]
                }
            })
            .state('trackDetail', {
                parent: 'entity',
                url: '/track/:id',
                data: {
                    roles: ['ROLE_USER'],
                    pageTitle: 'drinkApp.track.detail.title'
                },
                views: {
                    'content@': {
                        templateUrl: 'scripts/app/entities/track/track-detail.html',
                        controller: 'TrackDetailController'
                    }
                },
                resolve: {
                    translatePartialLoader: ['$translate', '$translatePartialLoader', function ($translate, $translatePartialLoader) {
                        $translatePartialLoader.addPart('track');
                        return $translate.refresh();
                    }]
                }
            });
    });
